+    template < typename T >
+    void svd(const Matrix < T > & A, Matrix < T > & U, Vector < T > & W, Matrix < T > & V)
+      {
+      int m = A.nrows(), n = A.ncols(), i, j, k, l, jj, nm;
+      const unsigned int max_its = 30;
+      bool flag;
+      Vector < T > rv1(n);
+      U = A;
+      W.resize(n);
+      V.resize(n, n);
+      T anorm, c, f, g, h, s, scale, x, y, z;
+      g = scale = anorm = (T)0.0;
+
+      // Householder reduction to bidiagonal form
+      for (i = 0; i < n; i++) {
+        l = i + 1;
+        rv1[i] = scale * g;
+        g = s = scale = (T)0.0;
+        if (i < m) {
+          for (k = i; k < m; k++) {
+            scale += fabs(U[k][i]);
+          }
+          if (scale != (T)0.0) {
