+            for (j = l; j < n; j++) {
+              s = (T)0.0;
+              for (k = i; k < m; k++) {
+                s += U[k][i] * U[k][j];
+              }
+              f = s / h;
+              for (k = i; k < m; k++) {
+                U[k][j] += f * U[k][i];
+              }
+            }
+            for (k = i; k < m; k++) {
+              U[k][i] *= scale;
+            }
+          }
+        }
+        W[i] = scale * g;
+        g = s = scale = (T)0.0;
+        if (i < m && i != n - 1) {
+          for (k = l; k < n; k++) {
+            scale += fabs(U[i][k]);
+          }
+          if (scale != (T)0.0) {
+            for (k = l; k < n; k++) {
+              U[i][k] /= scale;
+              s += U[i][k] * U[i][k];
+            }
+            f = U[i][l];
+            g = -sign(f) * sqrt(s);
+            h = f * g - s;
+            U[i][l] = f - g;
+            for (k = l; k < n; k++) {
+              rv1[k] = U[i][k] / h;
+            }
+            for (j = l; j < m; j++) {
+              s = (T)0.0;
+              for (k = l; k < n; k++) {
+                s += U[j][k] * U[i][k];
+              }
+              for (k = l; k < n; k++) {
+                U[j][k] += s * rv1[k];
+              }
+            }
+            for (k = l; k < n; k++) {
+              U[i][k] *= scale;
+            }
+          }
+        }
+        anorm = std::max(anorm, fabs(W[i]) + fabs(rv1[i]));
+      }
+      // Accumulation of right-hand transformations
+      for (i = n - 1; i >= 0; i--) {
+        if (i < n - 1) {
