+void State_Trotting::run()
+{
+  _posBody = _est->getPosition();
+  _velBody = _est->getVelocity();
+  _posFeet2BGlobal = _est->getPosFeet2BGlobal();
+  _posFeetGlobal = _est->getFeetPos();
+  _velFeetGlobal = _est->getFeetVel();
