+bool State_Trotting::checkStepOrNot()
+{
+  if ( (fabs(_vCmdBody(0)) > 0.03) ||
+    (fabs(_vCmdBody(1)) > 0.03) ||
+    (fabs(_posError(0)) > 0.08) ||
+    (fabs(_posError(1)) > 0.08) ||
+    (fabs(_velError(0)) > 0.05) ||
+    (fabs(_velError(1)) > 0.05) ||
+    (fabs(_dYawCmd) > 0.20) )
+  {
+    return true;
+  } else {
+    return false;
+  }
+}
