+void IOROS::sendCmd(const LowlevelCmd * lowCmd)
+{
+  for (int i(0); i < 12; ++i) {
+    _lowCmd.motorCmd[i].mode = lowCmd->motorCmd[i].mode;
+    _lowCmd.motorCmd[i].q = lowCmd->motorCmd[i].q;
