+    _lowCmd.motorCmd[i].tau = lowCmd->motorCmd[i].tau;
+    _lowCmd.motorCmd[i].Kd = lowCmd->motorCmd[i].Kd;
+    _lowCmd.motorCmd[i].Kp = lowCmd->motorCmd[i].Kp;
+  }
+  for (int m(0); m < 12; ++m) {
+    _servo_pub[m].publish(_lowCmd.motorCmd[m]);
+  }
+  ros::spinOnce();
+}
