+  inline void compute_d(
+    Vector < double > & d, const Matrix < double > & J,
+    const Vector < double > & np)
+  {
+    register int i, j, n = d.size();
+    register double sum;
+
+    /* compute d = H^T * np */
+    for (i = 0; i < n; i++) {
+      sum = 0.0;
+      for (j = 0; j < n; j++) {
+        sum += J[j][i] * np[j];
+      }
+      d[i] = sum;
+    }
+  }
+
+  inline void update_z(
+    Vector < double > & z, const Matrix < double > & J,
+    const Vector < double > & d, int iq)
+  {
+    register int i, j, n = z.size();
+
+    /* setting of z = H * d */
+    for (i = 0; i < n; i++) {
+      z[i] = 0.0;
+      for (j = iq; j < n; j++) {
+        z[i] += J[i][j] * d[j];
+      }
+    }
+  }
+
+  inline void update_r(
+    const Matrix < double > & R, Vector < double > & r,
+    const Vector < double > & d, int iq)
+  {
+    register int i, j;
+    register double sum;
+
+    /* setting of r = R^-1 d */
+    for (i = iq - 1; i >= 0; i--) {
+      sum = 0.0;
+      for (j = i + 1; j < iq; j++) {
+        sum += R[i][j] * r[j];
+      }
+      r[i] = (d[i] - sum) / R[i][i];
+    }
+  }
