+  bool add_constraint(
+    Matrix < double > & R, Matrix < double > & J, Vector < double > & d,
+    unsigned int & iq, double & R_norm)
+  {
+    unsigned int n = d.size();
+#ifdef TRACE_SOLVER
+    std::cout << "Add constraint " << iq << '/';
+#endif
+    register unsigned int i, j, k;
+    double cc, ss, h, t1, t2, xny;
+
+    /* we have to find the Givens rotation which will reduce the element
+      d[j] to zero.
+      if it is already zero we don't have to do anything, except of
+      decreasing j */
+    for (j = n - 1; j >= iq + 1; j--) {
+      /* The Givens rotation is done with the matrix (cc cs, cs -cc).
+      If cc is one, then element (j) of d is zero compared with element
+      (j - 1). Hence we don't have to do anything.
+      If cc is zero, then we just have to switch column (j) and column (j - 1)
+      of J. Since we only switch columns in J, we have to be careful how we
+      update d depending on the sign of gs.
+      Otherwise we have to apply the Givens rotation to these columns.
+      The i - 1 element of d has to be updated to h. */
+      cc = d[j - 1];
+      ss = d[j];
+      h = distance(cc, ss);
+      if (fabs(h) < std::numeric_limits < double > ::epsilon()) { // h == 0
+        continue;
+      }
+      d[j] = 0.0;
+      ss = ss / h;
+      cc = cc / h;
+      if (cc < 0.0) {
+        cc = -cc;
+        ss = -ss;
+        d[j - 1] = -h;
+      } else {
+        d[j - 1] = h;
+      }
+      xny = ss / (1.0 + cc);
+      for (k = 0; k < n; k++) {
+        t1 = J[k][j - 1];
+        t2 = J[k][j];
+        J[k][j - 1] = t1 * cc + t2 * ss;
+        J[k][j] = xny * (t1 + J[k][j - 1]) - t2;
+      }
+    }
+    /* update the number of constraints added*/
+    iq++;
+    /* To update R we have to put the iq components of the d vector
+      into column iq - 1 of R
+      */
+    for (i = 0; i < iq; i++) {
+      R[i][iq - 1] = d[i];
+    }
+#ifdef TRACE_SOLVER
+    std::cout << iq << std::endl;
+    print_matrix("R", R, iq, iq);
+    print_matrix("J", J);
+    print_vector("d", d, iq);
+#endif
+
+    if (fabs(d[iq - 1]) <= std::numeric_limits < double > ::epsilon() * R_norm) {
+      // problem degenerate
+      return false;
+    }
+    R_norm = std::max < double > (R_norm, fabs(d[iq - 1]));
+    return true;
+  }
